import React, {useState, useEffect} from 'react'
import{ Input, Popover, Radio, Modal, message } from "antd";
import{
  ArrowDownOutlined,
  DownOutlined,
  SettingOutlined,
} from "@ant-design/icons";

function Swap() {
const [slippage, setSlippage] = useState(2.5);


function handelSlippageChange(e){
  setSlippage(e.target.value);

}






const settings = (
  <>
    <div>Slippaage Tolerance</div>
    <div>
      <Radio.Group value={slippage} onChange={handelSlippageChange}>
        <Radio.Group value={0.5}>0.5%</Radio.Group> 
        <Radio.Group value={2.5}>2.5%</Radio.Group> 
        <Radio.Group value={5}>5%</Radio.Group> 
      </Radio.Group>
    </div>
  
  </>
    

)


  return (
    <div className="tradeBox">
      <div className="tradeBoxHeader">
        <h4>Swap</h4>
        <Popover 
        
          content = {settings}
          title = "settings"
          trigger="click"
          placement="bottomRight">

         <SettingOutlined className="cog" />
         </Popover>
      </div>
    </div>
  )
}


export default Swap
